  /* --- Global Scrollbar Hiding --- */
  body::-webkit-scrollbar,
  .hide-scrollbar::-webkit-scrollbar {
    display: none;
  }
  body,
  .hide-scrollbar {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }
  /* --- End Global Scrollbar Hiding --- */
}

